#pragma mark - NSNotification to select table cell

- (void) didSelectItemFromCollectionView:(NSNotification *)notification
{
    if(self.appDelegate.global_userInfo != NULL )
    {
       NSDictionary *courseObj = [notification object];
        if([[courseObj valueForKey:@"edge_id"] isEqualToString:@""])
        {
            UIAlertView *localAv = [[UIAlertView alloc]initWithTitle:[self.appDelegate.langObj get:@"ERROR" alter:@"Error"] message: [self.appDelegate.langObj get:@"COURSE_FILE_NA" alter:@"Please check your coursecode."] delegate:self cancelButtonTitle:[self.appDelegate.langObj get:@"OK" alter:@"Ok"] otherButtonTitles:nil, nil];
            [localAv show];
        }
        self.appDelegate.workingCourseObj = courseObj;
        
        if(![self.appDelegate.engineObj isCourseExist:[courseObj valueForKey:@"edge_id"]])
        {
            
//            if(self.dashProgrssView != NULL )
//            {
//                [self.dashProgrssView dismissWithClickedButtonIndex:0 animated:TRUE];
//                self.dashProgrssView = NULL;
//            }
//            self.dashProgrssView =[[UIAlertView alloc]initWithTitle:@"" message:[self.appDelegate.langObj get:@"PLEASE_WAIT" alter:@"Please wait."] delegate:self cancelButtonTitle:nil otherButtonTitles:nil];
//            [self.dashProgrssView show];
//            //[self.parentObj ];
            CourseScreen * courseSrObj = (CourseScreen *)self.parentObj;
            [courseSrObj showGlobalProgress];
            
            NSMutableDictionary * override= [[NSMutableDictionary alloc]init];
            [override setValue:[courseObj valueForKey:@"course_code"] forKey:JSON_COURSECODE];
            [override setValue:self.appDelegate.coursePack forKey:JSON_COURSEPACK];
            [override setValue: APPVERSION forKey:JSON_APPVERSION];
            
            
            NSMutableDictionary * reqObj = [[NSMutableDictionary alloc] init];
            [reqObj setValue:[self.appDelegate.global_userInfo valueForKey:DATABASE_TOKEN] forKey:JSON_TOKEN ];
            [reqObj setValue:JSON_COURSE_CHECK_DECREE forKey:JSON_DECREE ];
            [reqObj setObject:APPVERSION forKey:JSON_APPVERSION];
            [reqObj setObject:[[[UIDevice currentDevice] identifierForVendor] UUIDString] forKey:JSON_DEVICEID];
            [reqObj setObject:@"iOS" forKey:JSON_PLATFORM];
            [reqObj setObject:CLASS_NAME forKey:JSON_CALSS_NAME];
            [reqObj setObject:CLIENT forKey:JSON_CLIENT];
            [reqObj setValue:override forKey:JSON_PARAM];
            
            NSMutableDictionary * _reqObj = [[NSMutableDictionary alloc]init];
            [_reqObj setValue:SERVICE_COURSE_DOWNLOAD_FROM_EDASHBOARD forKey:@"SERVICE"];
            [_reqObj setValue:@"ORGMasterViewController" forKey:@"original_source"];
            [_reqObj setValue:courseObj forKey:@"original_data"];
            
            [self.appDelegate.engineObj.networkObj sendRequesttoServer:_reqObj:reqObj];
            
        }
        else{
            self.appDelegate.coursePack = @"EEDEMOEDGE";
            [self.appDelegate.engineObj setCourseCode:[courseObj valueForKey:@"course_code"]];
            self.appDelegate.courseCode = [courseObj valueForKey:@"course_code"];
            //self.appDelegate.courseName = [courseObj valueForKey:DATABASE_COURSE_NAME];
            self.appDelegate.viewMode = TRUE;
            [self.appDelegate gotoNextController:self controllerType:enum_dashboardController sendingObj:nil];
        }
    }
    else
    {
        
        mobileScreen *mobileObj = [[mobileScreen alloc]initWithNibName:@"mobileScreen" bundle:nil];
        [self.navigationController pushViewController:mobileObj animated:TRUE];
        
    }
    
}

