-(void)viewWillAppear:(BOOL)animated
{
    [super viewWillAppear:animated];
    [[NSNotificationCenter defaultCenter] addObserver:self
    selector:@selector(readDashResponse:)
        name:SERVICE_COURSE_DOWNLOAD_FROM_EDASHBOARD
      object:nil];
    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(didSelectItemFromCollectionView:) name:@"didSelectItemFromCollectionView" object:nil];
    
    
    
    
    
}


- (void)readDashResponse:(NSNotification *) notification
{
    
    if (![NSStringFromClass([self class]) isEqualToString: [[notification object]valueForKey:@"original_source"]] ){
        return;
    }
    dispatch_async(dispatch_get_main_queue(), ^{
        CourseScreen * courseSrObj = (CourseScreen *)self.parentObj;
        [courseSrObj hideGlobalProgress];
        
        if([[[notification object]valueForKey:NETWORKSTATUS] intValue] == [SUCCESSRESULT intValue] && [[[notification object]valueForKey:@"SERVICE"] isEqualToString:SERVICE_COURSE_DOWNLOAD_FROM_EDASHBOARD])
        {
            NSDictionary * temp = [[notification object]valueForKey:@"data"];
            if(temp!= NULL && [[temp valueForKey:@"retCode"] isEqualToString:SUCCESSJOSN])
            {
                NSDictionary * resUserData = [temp valueForKey:JSON_RETVAL];
                if(resUserData != NULL && [[resUserData valueForKey:NETWORKSTATUS]intValue] == 1 )
                {
                    self.appDelegate.coursePack = @"EEDEMOEDGE";
                    self.appDelegate.courseCode = [resUserData valueForKey:@"course_code"] ;
                    //self.appDelegate.courseName = [resUserData valueForKey:DATABASE_COURSE_NAME];
                    [self.appDelegate.engineObj setCourseCode:[resUserData valueForKey:@"course_code"]];
                    [self.appDelegate.engineObj DownloadNewCourseCode:[resUserData valueForKey:@"courseArr"]];
                    self.appDelegate.viewMode = TRUE;
                    [self.appDelegate gotoNextController:self controllerType:enum_dashboardController sendingObj:nil];
                }
            }
        }
        else
        {
            UIAlertController * alert = [UIAlertController
                                         alertControllerWithTitle:@""
                                         message: [[notification object]valueForKey:NETWORKDATA]
                                         preferredStyle:UIAlertControllerStyleAlert];
            
            
            [self presentViewController:alert animated:YES completion:nil];
            
            
            
            int duration = 2; // duration in seconds
            
            dispatch_after(dispatch_time(DISPATCH_TIME_NOW, duration * NSEC_PER_SEC), dispatch_get_main_queue(), ^{
                [alert dismissViewControllerAnimated:YES completion:nil];
            });
            return;
        }
        
    });
    
}


-(void)viewWillDisappear:(BOOL)animated
{
    [super viewWillDisappear:animated];
    NSNotificationCenter *center = [NSNotificationCenter defaultCenter];
    [center removeObserver:self name:SERVICE_COURSE_DOWNLOAD_FROM_EDASHBOARD object:nil];
     [[NSNotificationCenter defaultCenter] removeObserver:self name:@"didSelectItemFromCollectionView" object:nil];
    
}


